     ]

  }
  switchNameHandler = () => {
   // console.log("eas clicked");
   this.setState({
    persons: [
      { name:"Ant", age:30},
      { name:"Subhadeep",age:35},
      { name:"Aniket", age:27}
     ]

   })
  }
