import React from 'react';
//import { ProgressPlugin } from 'webpack';

const validation=(props)=>{
    let message="Too short message" ;
    if(props.input>= 5){
        message="Too long message"
    }
    return(
     <div>
         <p>{message}</p>
     </div>
    )
}
export default validation
