class App extends Component{

  state={
    person:[
    {id:"1", name:"ant",age: 30},
    {id:"2",name:"antara",age: 31},
    {id:"3",name:"aniket",age: 15}
    ],
    showPerson: false

  }




    nameChangeHandler=(event,id)=>{

      const personIndex= this.state.person.findIndex(p =>{
       return p.id=== id
      })
       const persons = {
        ...this.state.person[personIndex]
       }

       persons.name=event.target.value

       const person=[...this.state.person]

       person[personIndex]=persons


      this.setState({person:person})
    }

   deletePersonHandler=(personIndex)=>{
      const person= this.state.person
      person.splice(personIndex,1)
      this.setState( ([...person,{person:person}]))


   }


    toggleHandler=()=>{

      this.setState(prev => ({showPerson: !prev.showPerson}))

    }





  render(){
   const style={
     backgroundColor: "green",
     font: "inherit",
     border: '1px solid blue',
     padding: '8px',
     cursor: "pointer",
     color: "white"
   }


   let persons = null;
   if(this.state.showPerson){
   persons=(
      <div>
        {
          this.state.person.map((person,index) => {
           return <Person key={person.id}
           click={() => this.deletePersonHandler(index)}
           name={person.name} 
           age={person.age}
           change={(event) => this.nameChangeHandler(event,person.id)}/>

          })
        }
     </div>
    )
    style.backgroundColor= "red"
   }

   let classes=[]
    if(this.state.person.length <=2){
      classes.push('red')
    }
    if(this.state.person.length <=1){
      classes.push('bold')
    }


     return(
       <div className="App">
         <h1>Hi,I am React app</h1>
         <p className={classes.join(' ')}>This is really working</p>
         <button style={style}
         onClick={this.toggleHandler}
         >
            Switch Name
            </button>

         {persons}

       </div>
     )
   }
  }

export default RadiumApp


































































