import React from 'react';
//import { ProgressPlugin } from 'webpack';

function validation(props){
    let message="message too short";
    if(props.input >= 5){
        message="this is too long"
    }
    return(
     <div>
      <p>{message}</p> 
     </div>
    )
}

export default validation;
